}: {
  children: React.ReactNode;
}) {
  return (
    <html lang="en">
      <body className={inter.className}>
        <div className="fixed inset-0 -z-10">
          <Image src={heroBg} alt="Background" fill className="object-cover opacity-20" priority />
        </div>
        <LanguageProvider>
          <AuthProvider>
            {children}
            <Toaster />
